mapper.select:
SELECT MEMBER_CODE 
FROM MEMBER_INFO
WHERE ID=#{id} AND PW=#{pw}

SELECT CODE, ID, PW, NAME, NICKNAME, TEL, MEMBER_CODE
FROM MEMBER_INFO
WHERE MEMBER_CODE=#{member_code}

SELECT COUNT(*) AS COUNT
FROM PERMANENT_BAN
WHERE MEMBER_CODE=#{member_code}

SELECT COUNT(*) 
FROM MEMBER_INFO
WHERE ID = #{id}

SELECT COUNT(*) 
FROM WITHDRAWAL_MEMBER 
WHERE ID = #{id}

SELECT COUNT(*)
FROM MEMBER_INFO
WHERE NICKNAME = #{nickname}

SELECT COUNT(*)
FROM MEMBER_INFO
WHERE TEL = #{tel}
